@DefaultBean(type = FunctionMapper.class)
@Mapper
public class FunctionMapperImpl extends FunctionMapper
{

   @Override
   public Method resolveFunction(String prefix, String localName)
   {
      return null;
   }

}

public class FunctionMapperProvider
{
   @Produces
   @Mapper
   public FunctionMapper produceFunctionMapper()
   {
      return FacesContext.getCurrentInstance().getELContext().getFunctionMapper();
   }
}
